    function parseRecoverChainId(bytes memory encodedRecoverChainId) external pure returns (RecoverChainId memory rci);
}","keccak256":"0x0d35080aa52e3fd3899515c03bec6a074511c44ba4bc2351bc8f56c762333415","license":"Apache 2"},"contracts/interfaces/ITokenMinter.sol":{"content":"// SPDX-License-Identifier: Apache 2
